import React, { useState, useEffect } from "react";
import Context from "../../context/Context";
import { useContextSelector } from "use-context-selector";
import { Container, Row, Col, Button, ListGroup } from "react-bootstrap"

export default function ShowUsers() {
    const socket = useContextSelector(Context, (item) => item.socket);
    const [alluser, setAlluser] = useState([]);

    const get_data = () => {
        socket.emit("load_users");
        socket.on("send_users", (users) => {
            setAlluser(users);
        });
    };

    useEffect(() => {
        get_data();
        console.log(alluser);
    }, []);

    return (
		<Container className="d-flex flex-column mt-3 justify-content-center bg-light">
			<Row>
				<Col><h1 style={{ color: "red" }}>已上線的使用者</h1></Col>
			</Row>
			<ListGroup as="ul">
           		{alluser.map((user) => (
           			<ListGroup.Item as="li">{user.username}</ListGroup.Item>
				))}
			</ListGroup>
		</Container>
    );
}
