import React, { useState, useEffect, useRef } from "react";
import Context from "../../context/Context";
import { useContextSelector } from "use-context-selector";
import { Container, Row, Col, Button, Alert, Form } from "react-bootstrap";

export default function ShowUsers() {
    const socket = useContextSelector(Context, (item) => item.socket);
    const [alluser, setAlluser] = useState([]);
    const [receiver, setReceiver] = useState(false);
    const [message, setMessage] = useState("");

    const sendMessage = (e) => {
        e.preventDefault();
        console.log(message);
        setMessage("");
    };

    const get_data = () => {
        socket.emit("load_users");
        socket.on("send_users", (users) => {
            setAlluser(users);
        });
    };

    useEffect(() => {
        get_data();
        console.log(alluser);
    }, []);

    if (receiver) {
        return (
            <>
                <Alert
                    variant="light"
                    style={{ height: "500px" }}
                    onClose={() => setReceiver(false)}
                    dismissible
                >
                    <Alert.Heading>{receiver}</Alert.Heading>
                    <hr />
                    <p>
                        Change this and that and try again. Duis mollis, est non
                        commodo luctus, nisi erat porttitor ligula, eget lacinia
                        odio sem nec elit. Cras mattis consectetur purus sit
                        amet fermentum.
                    </p>
                </Alert>
                <Form onSubmit={sendMessage}>
                    <Form.Control
                        type="text"
                        placeholder={`text your message to ${receiver}`}
                        value={message}
                        onChange={(e) => {
                            setMessage(e.target.value);
                        }}
                    ></Form.Control>
                </Form>
            </>
        );
    }

    return (
        <Container
            className="mt-3 text-center justify-content-start"
            style={{ width: "300px" }}
        >
            <Row>
                <Col style={{ fontSize: "2.3rem", color: "green" }}>
                    Online User
                </Col>
            </Row>
            {alluser.map((user) =>
                user.id !== socket.id ? (
                    <Row className="mt-1">
                        <Col
                            className="d-grid"
                            style={{
                                background: "#1E1E1E",
                                color: "white",
                            }}
                        >
                            <Button
                                size="lg"
                                onClick={() => {
                                    setReceiver(user.username);
                                }}
                            >
                                {user.username}
                            </Button>
                        </Col>
                    </Row>
                ) : null
            )}
        </Container>
    );
}
