import React, { useState, useEffect } from "react";
import Context from "../../context/Context";
import { useContextSelector } from "use-context-selector";
import { Container, Row, Col, Button } from "react-bootstrap";

export default function ShowUsers() {
	const socket = useContextSelector(Context, (item) => item.socket);
	const [alluser, setAlluser] = useState([]);

	const get_data = () => {
		socket.emit("load_users");
		socket.on("send_users", (users) => {
			setAlluser(users);
		});
	};

	useEffect(() => {
		get_data();
		console.log(alluser);
	}, []);

	return (
		<Container
			className="mt-3 text-center justify-content-start"
			style={{ width: "300px" }}
		>
			<Row>
				<Col style={{ fontSize: "2.3rem", color: "green" }}>
					Online User
				</Col>
			</Row>
			<Container className="justify-content-start">
				{alluser.map((user) =>
					user.id !== socket.id ? (
						<Row className="mt-1">
							<Col
								className="d-grid"
								style={{
									background: "#1E1E1E",
									color: "white",
								}}
							>
								<Button size="lg">{user.username}</Button>
							</Col>
						</Row>
					) : null
				)}
			</Container>
		</Container>
	);
}
