import React, { useState, useEffect } from "react";
import Context from "../../context/Context";
import { useContextSelector } from "use-context-selector";
import { Container, Row, Col, Button } from "react-bootstrap";

export default function ShowUsers() {
	const socket = useContextSelector(Context, (item) => item.socket);
	const [alluser, setAlluser] = useState([]);

	const get_data = () => {
		socket.emit("load_users");
		socket.on("send_users", (users) => {
			setAlluser(users);
		});
	};

	useEffect(() => {
		get_data();
		console.log(alluser);
	}, []);

	return (
		<Container className="mt-3 text-center" style={{ width: "500px" }}>
			<Row>
				<Col>
					<h1 style={{ color: "green" }}>已上線的使用者</h1>
				</Col>
			</Row>
			{alluser.map((user) => (
				<Row className="mt-3">
					<Col
						as="button"
						style={{
							background: "#1E1E1E",
							color: "white",
							border: "1px solid rgb(100, 100, 100)",
						}}
					>
						<h3 className="bg-info">{user.username}</h3>
					</Col>
				</Row>
			))}
		</Container>
	);
}
