import React, { useState, useEffect, useRef } from "react";
import Context from "../../context/Context";
import { useContextSelector } from "use-context-selector";
import { Container, Row, Col, Button, Alert } from "react-bootstrap";

export default function ShowUsers() {
	const socket = useContextSelector(Context, (item) => item.socket);
	const [alluser, setAlluser] = useState([]);
	const [show, setShow] = useState(false);

	const get_data = () => {
		socket.emit("load_users");
		socket.on("send_users", (users) => {
			setAlluser(users);
		});
	};

	useEffect(() => {
		get_data();
		console.log(alluser);
	}, []);

	if (show) {
		return (
			<Alert variant="danger" onClose={() => setShow(false)} dismissible>
				<Alert.Heading>Oh snap! You got an error!</Alert.Heading>
				<p>
					Change this and that and try again. Duis mollis, est non
					commodo luctus, nisi erat porttitor ligula, eget lacinia
					odio sem nec elit. Cras mattis consectetur purus sit amet
					fermentum.
				</p>
			</Alert>
		);
	}

	return (
		<Container
			className="mt-3 text-center justify-content-start"
			style={{ width: "300px" }}
		>
			<Row>
				<Col style={{ fontSize: "2.3rem", color: "green" }}>
					Online User
				</Col>
			</Row>
			{alluser.map((user) =>
				user.id !== socket.id ? (
					<Row className="mt-1">
						<Col
							className="d-grid"
							style={{
								background: "#1E1E1E",
								color: "white",
							}}
						>
							<Button size="lg" onClick={() => { setShow(true) }}>{user.username}</Button>
						</Col>
					</Row>
				) : null
			)}
		</Container>
	);
}
