import React, { useState, useEffect } from "react";
import Context from "../../context/Context";
import { useContextSelector } from "use-context-selector";
import { Container, Row, Col, Button } from "react-bootstrap"

export default function ShowUsers() {
    const socket = useContextSelector(Context, (item) => item.socket);
    const [alluser, setAlluser] = useState([]);

    const get_data = () => {
        socket.emit("load_users");
        socket.on("send_users", (users) => {
            setAlluser(users);
        });
    };

    useEffect(() => {
        get_data();
        console.log(alluser);
    }, []);

    return (
		<Container className="mt-3">
			<Row>
				<Col><h1 style={{ color: "red" }}>已上線的使用者</h1></Col>
			</Row>
            {alluser.map((user) => (
                <Row className="mb-3 justify-contents-center">
                    <Col><Button style={{ width: "250px" }}>{user.username}</Button></Col>
                </Row>
            ))}
		</Container>
    );
}
