import React, { useState, useEffect, useRef, useContext } from "react";
import Context from "../../context/Context";
import { useContextSelector } from "use-context-selector";
import { Container, Row, Col, Button, Alert, Form } from "react-bootstrap";
import ChatBox from "./ChatBox";

export default function ShowUsers() {
	const socket = useContextSelector(Context, (item) => item.socket);
	const username = useContextSelector(Context, (item) => item.username);
	const [alluser, setAlluser] = useState([]);
	const [receiver, setReceiver] = useState(false);
	const [allmsg, setAllmsg] = useState({});

	const sortName = (username, receiver) => {
		return [username, receiver].sort().join("-");
	};

	const get_data = () => {
		socket.emit("load_users");
		socket.on("send_users", (users) => {
			setAlluser(users);
		});
	};

	const get_msg = () => {
		socket.on("get_msg", (MsgData) => {
			console.log(`${MsgData.sender}, ${MsgData.msg}`);
			const key = sortName(username, MsgData.sender)
			console.log(key)
			if (receiver === MsgData.sender) {
				MsgData = {...MsgData, view: true}
			} else {
				MsgData = {...MsgData, view: false}
			}
			console.log(MsgData)
			if (key in allmsg) {
				setAllmsg((prev) => {
					return {...prev, key: [...prev[key], MsgData]}
				})
			} else {
				setAllmsg({...allmsg, key: [MsgData]})
			}
			console.log("allmsg")
			console.log(allmsg)
		});
	};

	useEffect(() => {
		get_data();
		get_msg();
		return () => {
			socket.off("send_users");
		};
	}, []);

	return !receiver ? (
		<Container
			className="mt-3 text-center justify-content-start"
			style={{ width: "300px" }}
		>
			<Row>
				<Col style={{ fontSize: "2.3rem", color: "green" }}>
					Online User
				</Col>
			</Row>
			{alluser.map((user) =>
				user.id !== socket.id ? (
					<Row className="mt-1">
						<Col
							className="d-grid"
							style={{
								background: "#1E1E1E",
								color: "white",
							}}
						>
							<Button
								variant="info"
								size="lg"
								onClick={() => {
									setReceiver(user.username);
								}}
							>
								{user.username}
							</Button>
						</Col>
					</Row>
				) : null
			)}
		</Container>
	) : (
		<ChatBox receiver={receiver} setReceiver={setReceiver} />
	);
}
