import React, { useState } from "react";
import { Form, Alert, Button, Container, Row } from "react-bootstrap";
import Context from "../../context/Context";
import { useContextSelector } from "use-context-selector";

export default function ChatBox({ receiver, setReceiver }) {
    const [message, setMessage] = useState("");
    const socket = useContextSelector(Context, (item) => item.socket);
    const username = useContextSelector(Context, (item) => item.username);
    const [showmsg, setShowMsg] = useState("");

    const sendMessage = (e) => {
        setShowMsg(message);
        e.preventDefault();
        console.log(message);
        setMessage("");
    };

    return (
        <Container>
            <Alert
                variant="light"
                style={{ height: "500px" }}
                onClose={() => setReceiver(false)}
                dismissible
            >
                <Alert.Heading>{receiver}</Alert.Heading>
                <hr />
                <Container></Container>
            </Alert>
            <Form onSubmit={sendMessage}>
                <Form.Control
                    type="text"
                    placeholder={`text your message to ${receiver}`}
                    value={message}
                    onChange={(e) => {
                        setMessage(e.target.value);
                    }}
                ></Form.Control>
            </Form>
        </Container>
    );
}
